import os
import json
import time
import datetime
from web3 import Web3
from sys import stderr
from loguru import logger
from web3.exceptions import TransactionNotFound, ContractLogicError


def time_left():
    # n = 1659002390 - int(time.time())
    # time_format = time.strftime("%dd %Hh %Mm %Ssec", time.gmtime(n))
    dt = datetime.datetime(2022, 7, 28, 19, 9, 55) - datetime.datetime.now()
    mm, ss = divmod(dt.seconds, 60)
    hh, mm = divmod(mm, 60)
    time_format = f'{hh} hours {mm} mins {ss} seconds left'
    return(time_format)


def take_gas():
    timed_gas = web3.eth.gasPrice
    if timed_gas <= 5000000000:
        return(6100000000)
    else:
        return(timed_gas * 1.34)


def amount_swapped(hash):
    try:
        tx_json = json.loads(web3.toJSON(web3.eth.getTransactionReceipt(hash)))
        timed_amount = int(tx_json['logs'][-3]['data'].removeprefix('0x'), 16)
        if timed_amount >= 1000000000000:
            swapped_amount = round((float(web3.fromWei((timed_amount), 'ether'))), 2)
        else:
            swapped_amount = str(timed_amount) + ' smt wrong'
        return(swapped_amount)
    except Exception as error:
        return(error)

def wait_for_tx(hash, a):
    try:
        sta = json.loads(web3.toJSON(web3.eth.getTransactionReceipt(hash)))['status']
        while not (a == 1020 or sta > -1):
            sta = json.loads(web3.toJSON(web3.eth.getTransactionReceipt(hash)))['status']
            a = a + 1
            logger.info(f'NOTHING HAPPENS. STA {sta}')
        if (sta == 1):
            logger.opt(colors=True).info(f'TX <green>SUBMITTED</green>: https://bscscan.com/tx/{hash}')
        elif sta == 0:
            logger.opt(colors=True).error(f'<red>TX FAILED</red>!!! https://bscscan.com/tx/{hash}')
        else:
            logger.opt(colors=True).error(f'<red>SOMETHING WRONG NEED YOUR HELP</red>!!! https://bscscan.com/tx/{hash}')
    except TransactionNotFound:
        wait_for_tx(hash, a+1)
    except Exception as error:
        logger.error(f'Error: {error}')
        wait_for_tx(hash, a+1)


def claim_impossible():
    try:
        wormhole_contract.functions.withdraw().buildTransaction()
        # web3.exceptions.ContractLogicError: execution reverted: cannot withdraw yet
        # END IN 1658916056 (https://bscscan.com/address/0x8e930597d1e863698fb54becc16085dce683c82b#readContract)
    except ContractLogicError as error:
        if str(error) == 'execution reverted: cannot withdraw yet':
            # if time.time() == 1659002390
            clear()
            logger.info(f'Waiting for able to claim... Time left {time_left()} (+ 5 sec)')
            claim_impossible()
        else:
            logger.opt(colors=True).info(f'<green>Can claim</green>, sending tx...\ngood error is <green>{str(error)}</green>')
            claim_tx = wormhole_contract.functions.withdraw().buildTransaction({
                'from': main_acc,
                'value': 0,
                'gas': 350000,
                'gasPrice': take_gas(),
                'nonce': web3.eth.getTransactionCount(main_acc)
            })
            updated_gas = web3.eth.estimate_gas(claim_tx)
            claim_tx.update({'gas': int(updated_gas * 2.1)})

            sign_tx = web3.eth.account.sign_transaction(claim_tx, private_key)
            send_tx = web3.eth.send_raw_transaction(sign_tx.rawTransaction)
            tx_hash = web3.toHex(send_tx)
            return(tx_hash)

